    // 价格范围定义
    private static final double[] 价格范围 = {500000, 1000000, 1500000, 2000000, 3000000, 5000000, Double.MAX_VALUE};
    private static final String[] 范围名称 = {"50万以下", "50-100万", "100-150万", "150-200万", "200-300万", "300-500万", "500万以上"};
    
    public static void main(String[] args) {
        System.out.println("=== 基于国信达总价的房价范围分析 ===");
        
        int n = 中介单价.length;
        System.out.println("数据量: " + n);
        
        // 按价格范围分组数据
        List<PriceRangeData>[] 分组数据 = groupDataByPriceRange();
        
        // 分析每个价格范围
        for (int i = 0; i < 价格范围.length; i++) {
            if (分组数据[i].size() > 0) {
                analyzePriceRange(分组数据[i], 范围名称[i], i);
            }
        }
        
        // 生成综合公式
        generateComprehensiveFormula(分组数据);
        
        // 保存结果
        saveResultsToFile(分组数据);
    }
    
    // 按价格范围分组数据
    private static List<PriceRangeData>[] groupDataByPriceRange() {
        @SuppressWarnings("unchecked")
        List<PriceRangeData>[] 分组数据 = new List[价格范围.length];
        
        for (int i = 0; i < 分组数据.length; i++) {
            分组数据[i] = new ArrayList<>();
        }
        
        for (int i = 0; i < 中介单价.length; i++) {
            double 总价 = 国信达总价[i];
            int 范围索引 = 0;
            
            for (int j = 0; j < 价格范围.length; j++) {
                if (总价 < 价格范围[j]) {
                    范围索引 = j;
                    break;
                }
            }
            
            分组数据[范围索引].add(new PriceRangeData(中介单价[i], 国信达单价[i], 国信达总价[i]));
        }
        
        return 分组数据;
    }
    
    // 分析单个价格范围
    private static void analyzePriceRange(List<PriceRangeData> 数据, String 范围名称, int 范围索引) {
        if (数据.size() < 3) {
            System.out.println("\n" + 范围名称 + " - 数据量不足，跳过分析");
            return;
        }
        
        System.out.println("\n=== " + 范围名称 + " 分析 ===");
        System.out.println("数据量: " + 数据.size());
        
        // 提取数据
        double[] 中介单价数组 = new double[数据.size()];
        double[] 国信达单价数组 = new double[数据.size()];
        double[] 国信达总价数组 = new double[数据.size()];
        
        for (int i = 0; i < 数据.size(); i++) {
            PriceRangeData d = 数据.get(i);
            中介单价数组[i] = d.中介单价;
            国信达单价数组[i] = d.国信达单价;
            国信达总价数组[i] = d.国信达总价;
        }
        
        // 计算基本统计
        double 中介单价_平均 = calculateMean(中介单价数组);
        double 国信达单价_平均 = calculateMean(国信达单价数组);
        double 国信达总价_平均 = calculateMean(国信达总价数组);
        
        System.out.printf("中介单价平均值: %.2f%n", 中介单价_平均);
        System.out.printf("国信达单价平均值: %.2f%n", 国信达单价_平均);
        System.out.printf("国信达总价平均值: %.2f%n", 国信达总价_平均);
        
        // 线性回归分析
        double 斜率 = calculateSlope(国信达单价数组, 中介单价数组, 国信达单价_平均, 中介单价_平均);
        double 截距 = 中介单价_平均 - 斜率 * 国信达单价_平均;
        
        // 计算R²
        double[] 预测值 = new double[中介单价数组.length];
        for (int i = 0; i < 预测值.length; i++) {
            预测值[i] = 斜率 * 国信达单价数组[i] + 截距;
        }
        double r2 = calculateRSquared(中介单价数组, 预测值, 中介单价_平均);
        double mae = calculateMAE(中介单价数组, 预测值);
        
        System.out.printf("线性回归公式: 中介单价 = %.2f + %.4f × 国信达单价%n", 截距, 斜率);
        System.out.printf("R²: %.4f, MAE: %.2f%n", r2, mae);
        
        // 基于总价的回归分析
        double 总价斜率 = calculateSlope(国信达总价数组, 中介单价数组, 国信达总价_平均, 中介单价_平均);
        double 总价截距 = 中介单价_平均 - 总价斜率 * 国信达总价_平均;
        
        double[] 总价预测值 = new double[中介单价数组.length];
        for (int i = 0; i < 总价预测值.length; i++) {
            总价预测值[i] = 总价斜率 * 国信达总价数组[i] + 总价截距;
        }
        double 总价r2 = calculateRSquared(中介单价数组, 总价预测值, 中介单价_平均);
        double 总价mae = calculateMAE(中介单价数组, 总价预测值);
        
        System.out.printf("总价回归公式: 中介单价 = %.2f + %.6f × 国信达总价%n", 总价截距, 总价斜率);
        System.out.printf("总价R²: %.4f, 总价MAE: %.2f%n", 总价r2, 总价mae);
        
        // 保存范围数据
        数据.get(0).setRegressionParams(截距, 斜率, r2, mae, 总价截距, 总价斜率, 总价r2, 总价mae);
    }
    
    // 生成综合公式
    private static void generateComprehensiveFormula(List<PriceRangeData>[] 分组数据) {
        System.out.println("\n=== 综合价格范围公式 ===");
        
        for (int i = 0; i < 分组数据.length; i++) {
            if (分组数据[i].size() > 3) {
                PriceRangeData 样本 = 分组数据[i].get(0);
                System.out.printf("%s: 中介单价 = %.2f + %.4f × 国信达单价 (R²=%.4f)%n", 
                    范围名称[i], 样本.截距, 样本.斜率, 样本.r2);
                System.out.printf("        或: 中介单价 = %.2f + %.6f × 国信达总价 (R²=%.4f)%n", 
                    样本.总价截距, 样本.总价斜率, 样本.总价r2);
            }
        }
    }
    
    // 计算平均值
    private static double calculateMean(double[] values) {
        double sum = 0;
        for (double value : values) {
            sum += value;
        }
        return sum / values.length;
    }
    
    // 计算斜率
    private static double calculateSlope(double[] x, double[] y, double xMean, double yMean) {
        double numerator = 0;
        double denominator = 0;
        
        for (int i = 0; i < x.length; i++) {
            numerator += (x[i] - xMean) * (y[i] - yMean);
            denominator += Math.pow(x[i] - xMean, 2);
        }
        
        return numerator / denominator;
    }
    
    // 计算R²
    private static double calculateRSquared(double[] actual, double[] predicted, double actualMean) {
        double ssRes = 0;
        double ssTot = 0;
        
        for (int i = 0; i < actual.length; i++) {
            ssRes += Math.pow(actual[i] - predicted[i], 2);
            ssTot += Math.pow(actual[i] - actualMean, 2);
        }
        
        return 1 - (ssRes / ssTot);
    }
    
    // 计算MAE
    private static double calculateMAE(double[] actual, double[] predicted) {
        double sum = 0;
        for (int i = 0; i < actual.length; i++) {
            sum += Math.abs(actual[i] - predicted[i]);
        }
        return sum / actual.length;
    }
    
    // 保存结果到文件
    private static void saveResultsToFile(List<PriceRangeData>[] 分组数据) {
        try (PrintWriter writer = new PrintWriter(new FileWriter("house_price_range_formula.txt"))) {
            writer.println("=== 基于国信达总价的房价范围分析结果 ===");
            writer.println();
            
            for (int i = 0; i < 分组数据.length; i++) {
                if (分组数据[i].size() > 3) {
                    PriceRangeData 样本 = 分组数据[i].get(0);
                    writer.println("价格范围: " + 范围名称[i]);
                    writer.println("数据量: " + 分组数据[i].size());
                    writer.printf("单价公式: 中介单价 = %.2f + %.4f × 国信达单价 (R²=%.4f)%n", 
                        样本.截距, 样本.斜率, 样本.r2);
                    writer.printf("总价公式: 中介单价 = %.2f + %.6f × 国信达总价 (R²=%.4f)%n", 
                        样本.总价截距, 样本.总价斜率, 样本.总价r2);
                    writer.println();
                }
            }
            
            System.out.println("\n结果已保存到 house_price_range_formula.txt");
        } catch (IOException e) {
            System.err.println("保存文件时出错: " + e.getMessage());
        }
    }
    
    // 数据类
    static class PriceRangeData {
        double 中介单价, 国信达单价, 国信达总价;
        double 截距, 斜率, r2, mae;
        double 总价截距, 总价斜率, 总价r2, 总价mae;
        
        public PriceRangeData(double 中介单价, double 国信达单价, double 国信达总价) {
            this.中介单价 = 中介单价;
            this.国信达单价 = 国信达单价;
            this.国信达总价 = 国信达总价;
        }
        
        public void setRegressionParams(double 截距, double 斜率, double r2, double mae,
                                      double 总价截距, double 总价斜率, double 总价r2, double 总价mae) {
            this.截距 = 截距;
            this.斜率 = 斜率;
            this.r2 = r2;
            this.mae = mae;
            this.总价截距 = 总价截距;
            this.总价斜率 = 总价斜率;
            this.总价r2 = 总价r2;
            this.总价mae = 总价mae;
        }
    }
